import 'package:demo_movies_map_profile/common/const/navigation_paths.dart';import 'package:demo_movies_map_profile/features/home/framework/presentation/page/home_page.dart';import 'package:demo_movies_map_profile/features/map/framework/presentation/page/map_page.dart';import 'package:flutter/material.dart';import 'package:go_router/go_router.dart';import 'package:riverpod/riverpod.dart';final navigationProvider = Provider<GoRouter>((ref) {  return GoRouter(    initialLocation: initialPage,    routes: [      StatefulShellRoute.indexedStack(        builder: (context, state, navigationShell) {          return HomePage(navigationShell);        },        branches: [          StatefulShellBranch(            routes: [              GoRoute(                  name: moviesPage,                  path: initialPage,                  builder: (BuildContext context, GoRouterState state) {                    return Container(color: Colors.red);                  }),            ],          ),          StatefulShellBranch(            routes: [              GoRoute(                  name: mapPage,                  path: mapPagePath,                  builder: (BuildContext context, GoRouterState state) {                    return const MapPage();                  }),            ],          ),          StatefulShellBranch(            routes: [              GoRoute(                  name: profilePage,                  path: profilePagePath,                  builder: (BuildContext context, GoRouterState state) {                    return Container(color: Colors.black);                  }),            ],          )        ],      ),    ],  );});