import 'package:demo_movies_map_profile/common/const/navigation_paths.dart';import 'package:flutter/material.dart';import 'package:go_router/go_router.dart';import 'package:riverpod/riverpod.dart';final navigationProvider = Provider<GoRouter>((ref) {  return GoRouter(    initialLocation: initialPage,    routes: [      GoRoute(          name: homePage,          path: initialPage,          builder: (BuildContext context, GoRouterState state) {            return Container();          }),      GoRoute(          name: moviesPage,          path: "$initialPage$moviesPage",          builder: (BuildContext context, GoRouterState state) {            return Container();          }),      GoRoute(          name: mapPage,          path: "$initialPage$mapPage",          builder: (BuildContext context, GoRouterState state) {            return Container();          }),      GoRoute(          name: profilePage,          path: "$initialPage$profilePage",          builder: (BuildContext context, GoRouterState state) {            return Container();          }),    ],  );});